import { useState } from "react";
import { useAuth } from "..//AuthContext";

export default function ReviewForm({ onSubmit }) {
  const { currentUser } = useAuth();

  const [rating, setRating] = useState(5);
  const [text, setText] = useState("");

  const handleSubmit = () => {
    if (!text.trim()) return;

    const newReview = {
      id: Date.now(),
      userId: currentUser.id,
      rating,
      text
    };

    onSubmit(newReview);

    setText("");
    setRating(5);
  };

  return (
    <div className="bg-gray-800 border border-gray-700 p-5 rounded-xl mt-6">
      <h3 className="text-xl font-semibold text-white mb-4">Write a Review</h3>

      <select
        className="w-full p-2 mb-3 bg-gray-700 text-white rounded"
        value={rating}
        onChange={(e) => setRating(Number(e.target.value))}
      >
        <option value="5">⭐⭐⭐⭐⭐ 5</option>
        <option value="4">⭐⭐⭐⭐ 4</option>
        <option value="3">⭐⭐⭐ 3</option>
        <option value="2">⭐⭐ 2</option>
        <option value="1">⭐ 1</option>
      </select>

      <textarea
        className="w-full h-28 p-3 rounded bg-gray-700 text-white border border-gray-600"
        placeholder="Write your review..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />

      <button
        onClick={handleSubmit}
        className="mt-3 px-4 py-2 bg-slate-700 hover:bg-slate-600 rounded text-white"
      >
        Submit Review
      </button>
    </div>
  );
}
