import { useParams } from "react-router-dom";
import { hostels } from "../Components/";

export default function HostelDetails() {
  const { id } = useParams();
  const hostel = hostels.find((h) => h.id === Number(id));

  if (!hostel) {
    return (
      <div className="bg-gray-900 text-white min-h-screen flex items-center justify-center">
        <h1 className="text-2xl">Hostel Not Found</h1>
      </div>
    );
  }

  const avgRating =
    hostel.reviews?.length > 0
      ? (
          hostel.reviews.reduce((sum, r) => sum + r.rating, 0) /
          hostel.reviews.length
        ).toFixed(1)
      : "N/A";

  return (
    <div className="bg-gray-900 text-white min-h-screen p-8">
      <div className="max-w-5xl mx-auto grid md:grid-cols-2 gap-10">
        {/* Image */}
        <div>
          <img
            src={hostel.image}
            alt={hostel.name}
            className="rounded-lg shadow-lg w-full h-80 object-cover"
          />
        </div>

        {/* Details */}
        <div>
          <h1 className="text-4xl font-bold mb-2">{hostel.name}</h1>
          <p className="text-gray-300 text-lg mb-4">{hostel.area}</p>

          <div className="text-gray-400 mb-6">
            <p>Gender: {hostel.gender}</p>
            <p>Profession: {hostel.profession}</p>
            <p>Rent: <span className="text-white font-bold">PKR {hostel.rent.toLocaleString()}</span></p>
            <p className="mt-2">
              ⭐ Average Rating: <span className="text-yellow-400">{avgRating}</span>
            </p>
          </div>

          <p className="text-gray-300 leading-relaxed">
            {hostel.description || "No description provided by the owner."}
          </p>

          <button className="mt-6 px-6 py-3 bg-slate-700 hover:bg-slate-600 rounded-lg w-full md:w-auto">
            Contact Owner
          </button>
        </div>
      </div>

      {/* Reviews Section */}
      <div className="max-w-5xl mx-auto mt-16">
        <h2 className="text-3xl font-bold mb-4">Reviews</h2>

        {hostel.reviews?.length === 0 ? (
          <p className="text-gray-400">No reviews yet.</p>
        ) : (
          <div className="space-y-4">
            {hostel.reviews.map((rev, i) => (
              <div
                key={i}
                className="bg-gray-800 p-4 rounded-lg border border-gray-700"
              >
                <p className="text-yellow-400">⭐ {rev.rating}/5</p>
                <p className="text-gray-300 mt-1">{rev.comment || "No comment"}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
