export default function HostelCard({ hostel }) {
  const avgRating =
    hostel.reviews?.length > 0
      ? (
          hostel.reviews.reduce((sum, r) => sum + r.rating, 0) /
          hostel.reviews.length
        ).toFixed(1)
      : "N/A";

  return (
    <div className="bg-gray-800  rounded-lg shadow-lg border border-gray-700 hover:scale-[1.02] transition">
      <img
        src={hostel.image}
        alt={hostel.name}
        className="w-full h-48 object-cover rounded-md"
      />

      <h3 className="text-xl text-white font-semibold mt-3">{hostel.name}</h3>
      <p className="text-gray-300 text-sm">{hostel.area}</p>

      <p className="text-slate-400 text-sm mt-2">
        {hostel.gender} | {hostel.profession}
      </p>

      <p className="text-white font-bold mt-2">
        PKR {hostel.rent.toLocaleString()}
      </p>

      <p className="text-gray-300 text-sm mt-1">
        ‚≠ê Rating: <span className="text-yellow-400">{avgRating}</span>
      </p>

      <button className="mt-4 px-4 py-2 bg-slate-700 hover:bg-slate-600 text-white w-full rounded-lg">
        View Details
      </button>
    </div>
  );
}
