import { Link, NavLink } from "react-router-dom";

export default function Navbar() {
  return (
    <header className="bg-gray-900 border-b border-gray-700 shadow-lg sticky top-0 z-50">
      <nav className="container mx-auto px-6 py-3 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="flex items-center gap-2 text-white font-bold text-2xl">
          <img
            src="../Images/"
            alt="Hostel Facilitator"
            className="h-12 w-12"
          />
        </Link>

        {/* Navigation Links */}
        <div className="hidden md:flex gap-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `text-sm font-medium ${
                isActive
                  ? "text-white border-b-2 border-slate-500"
                  : "text-gray-300 hover:text-white"
              }`
            }
          >
            Home
          </NavLink>

          <NavLink
            to="/hostels"
            className={({ isActive }) =>
              `text-sm font-medium ${
                isActive
                  ? "text-white border-b-2 border-slate-500"
                  : "text-gray-300 hover:text-white"
              }`
            }
          >
            Hostels
          </NavLink>

          <NavLink
            to="/about"
            className={({ isActive }) =>
              `text-sm font-medium ${
                isActive
                  ? "text-white border-b-2 border-slate-500"
                  : "text-gray-300 hover:text-white"
              }`
            }
          >
            About
          </NavLink>
        </div>
      </nav>
    </header>
  );
}
