import { useState } from "react";
import { Navigate } from "react-router-dom";
import { useAuth } from "../Components/AuthContext";
import { hostels as hostelData } from "../data/hostelMock";
import { users } from "../data/users";

export default function OwnerDashboard() {
  const { currentUser } = useAuth();


  // Make a runtime copy (so we can mutate)
  const [hostels, setHostels] = useState(hostelData);

  // Only owners allowed
  if (!currentUser || currentUser.role !== "owner") {
    return <Navigate to="/login" replace />;
  }

  const myHostels = hostels.filter((h) => h.ownerId === currentUser.id);

  // Add answer to a question (runtime only)
  const answerQuestion = (hostelId, questionId, answerText) => {
    setHostels(prev =>
      prev.map(h => {
        if (h.id !== hostelId) return h;

        return {
          ...h,
          questions: h.questions.map(q =>
            q.id === questionId ? { ...q, answer: answerText } : q
          )
        };
      })
    );
  };

  return (
    <div className="bg-gray-900 min-h-screen text-white p-6">
      <div className="max-w-6xl mx-auto">

        <h1 className="text-4xl font-bold mb-6">Owner Dashboard</h1>
        
        {myHostels.length === 0 && (
          <p className="text-gray-400">You do not own any hostels yet.</p>
        )}

        <div className="space-y-6">
          {myHostels.map(hostel => (
            <HostelCard
              key={hostel.id}
              hostel={hostel}
              users={users}
              answerQuestion={answerQuestion}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

/* ---------------- HOSTEL CARD ---------------- */

function HostelCard({ hostel, users, answerQuestion }) {
  return (
    <div className="bg-gray-800 p-6 rounded-xl border border-gray-700 shadow-lg">
      
      {/* Header */}
      <div className="flex justify-between items-start mb-4">
        <div>
          <h2 className="text-2xl font-bold">{hostel.name}</h2>
          <p className="text-gray-400">{hostel.area}</p>
        </div>

        <span className={`px-3 py-1 rounded-lg ${
          hostel.status === "approved"
            ? "bg-green-700 text-green-200"
            : "bg-yellow-700 text-yellow-200"
        }`}>
          {hostel.status.toUpperCase()}
        </span>
      </div>

      {/* Reviews */}
      <h3 className="text-xl font-semibold mt-6 mb-2">Reviews</h3>
      {hostel.reviews.length === 0 ? (
        <p className="text-gray-400">No reviews yet.</p>
      ) : (
        <div className="space-y-3">
          {hostel.reviews.map(r => {
            const user = users.find(u => u.id === r.userId);
            return (
              <div key={r.id} className="bg-gray-700 p-3 rounded-lg border border-gray-600">
                <p className="font-semibold">⭐ {r.rating} — {user?.name || "User"}</p>
                <p className="text-gray-300">{r.text}</p>
              </div>
            );
          })}
        </div>
      )}

      {/* Questions */}
      <h3 className="text-xl font-semibold mt-8 mb-2">Questions</h3>
      {hostel.questions.length === 0 ? (
        <p className="text-gray-400">No questions yet.</p>
      ) : (
        hostel.questions.map(q => (
          <QuestionItem
            key={q.id}
            question={q}
            users={users}
            onAnswer={text => answerQuestion(hostel.id, q.id, text)}
          />
        ))
      )}

    </div>
  );
}

/* ---------------- QUESTION ITEM ---------------- */

function QuestionItem({ question, users, onAnswer }) {
  const user = users.find(u => u.id === question.userId);
  const [answerText, setAnswerText] = useState("");

  return (
    <div className="bg-gray-700 p-4 rounded-lg border border-gray-600 mb-4">

      <p className="font-semibold">Q: {question.text}</p>
      <p className="text-xs text-gray-400 mb-2">Asked by {user?.name || "User"}</p>

      {question.answer ? (
        <div className="ml-3 border-l-2 border-slate-500 pl-3">
          <p className="font-semibold">A: {question.answer}</p>
        </div>
      ) : (
        <div className="mt-3 ml-3">
          <textarea
            rows="2"
            className="w-full bg-gray-800 p-2 rounded-md border border-gray-600"
            placeholder="Type your answer..."
            value={answerText}
            onChange={e => setAnswerText(e.target.value)}
          />

          <button
            onClick={() => {
              if (answerText.trim()) {
                onAnswer(answerText);
                setAnswerText("");
              }
            }}
            className="px-3 py-1 mt-2 bg-slate-600 hover:bg-slate-700 rounded-md"
          >
            Submit Answer
          </button>
        </div>
      )}
    </div>
  );
}
