import { useParams } from "react-router-dom";
import { hostels } from "../data/hostels";
import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import ReviewList from "../Components/ReviewList";
import ReviewForm from "../Components/ReviewForm";

export default function HostelDetail() {
  const { id } = useParams();
  const hostel = hostels.find((h) => h.id === Number(id));

  const { currentUser } = useAuth();
  const [reviews, setReviews] = useState(hostel.reviews || []);

  const addReview = (newReview) => {
    setReviews([newReview, ...reviews]);
  };

  if (!hostel) return <p className="text-white">Hostel not found</p>;

  return (
    <div className="bg-gray-900 min-h-screen text-white p-6">
      <div className="max-w-4xl mx-auto">

        <h1 className="text-4xl font-bold mb-2">{hostel.name}</h1>
        <p className="text-gray-300">{hostel.area}</p>

        {/* Review List */}
        <ReviewList reviews={reviews} />

        {/* Review Form (only for logged-in users) */}
        {currentUser && currentUser.role === "user" && (
          <ReviewForm onSubmit={addReview} />
        )}

        {!currentUser && (
          <p className="text-gray-400 mt-4">
            <i>You must be logged in to post a review.</i>
          </p>
        )}
      </div>
    </div>
  );
}
