import { useState } from "react";

export default function AddHostelModal({ closeModal, addHostel, currentUser }) {
  // Form state
  const [name, setName] = useState("");
  const [area, setArea] = useState("");
  const [rent, setRent] = useState("");
  const [gender, setGender] = useState("Male");
  const [profession, setProfession] = useState("Student");
  const [desc, setDesc] = useState("");

  // Amenity checklist
  const amenityList = [
    "Wi-Fi",
    "Laundry",
    "Mess",
    "Parking",
    "Security",
    "AC Rooms",
    "CCTV",
    "Housekeeping",
    "Study Area",
    "Common Room",
    "Kitchenette"
  ];

  const [selectedAmenities, setSelectedAmenities] = useState([]);

  const toggleAmenity = (amenity) => {
    setSelectedAmenities((prev) =>
      prev.includes(amenity)
        ? prev.filter((a) => a !== amenity)
        : [...prev, amenity]
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newHostel = {
      id: Date.now(),
      name,
      area,
      rent: Number(rent),
      gender,
      profession,
      description: desc,
      image: "https://placehold.co/600x400/4b5563/ffffff?text=New+Hostel",
      amenities: selectedAmenities,
      ownerId: currentUser.id,
      status: "pending",
      reviews: [],
      questions: [],
      views: 0,
      shortlists: 0,
    };

    addHostel(newHostel);
    closeModal();
  };

  return (
    <div
      className="modal-backdrop fixed inset-0 flex items-center justify-center p-4 bg-black/50"
      onClick={closeModal}
    >
      <div
        className="modal bg-gray-800 rounded-lg shadow-xl w-full max-w-lg p-6 border border-gray-700max-h-[80vh]overflow-y-auto
        "
        onClick={(e) => e.stopPropagation()}
        >

        <h3 className="text-2xl font-bold text-white mb-4">List a New Hostel</h3>

        <form onSubmit={handleSubmit} className="space-y-4">

          {/* Row 1 */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-300">
                Hostel Name
              </label>
              <input
                type="text"
                className="w-full p-2 border border-gray-600 bg-gray-700 text-white rounded-md"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300">
                Area
              </label>
              <input
                type="text"
                className="w-full p-2 border border-gray-600 bg-gray-700 text-white rounded-md"
                value={area}
                onChange={(e) => setArea(e.target.value)}
                required
              />
            </div>
          </div>

          {/* Rent */}
          <div>
            <label className="block text-sm font-medium text-gray-300">
              Monthly Rent (PKR)
            </label>
            <input
              type="number"
              className="w-full p-2 border border-gray-600 bg-gray-700 text-white rounded-md"
              value={rent}
              onChange={(e) => setRent(e.target.value)}
              required
            />
          </div>

          {/* Gender + Profession */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-300">
                Gender
              </label>
              <select
                className="w-full p-2 border border-gray-600 bg-gray-700 text-white rounded-md"
                value={gender}
                onChange={(e) => setGender(e.target.value)}
              >
                <option>Male</option>
                <option>Female</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300">
                Profession
              </label>
              <select
                className="w-full p-2 border border-gray-600 bg-gray-700 text-white rounded-md"
                value={profession}
                onChange={(e) => setProfession(e.target.value)}
              >
                <option>Student</option>
                <option>Professional</option>
              </select>
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-gray-300">
              Description
            </label>
            <textarea
              rows="3"
              className="w-full p-2 border border-gray-600 bg-gray-700 text-white rounded-md"
              value={desc}
              onChange={(e) => setDesc(e.target.value)}
              required
            ></textarea>
          </div>

          {/* AMENITIES CHECKLIST */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Amenities
            </label>

            <div className="grid grid-cols-2 gap-2">
              {amenityList.map((amenity) => (
                <label
                  key={amenity}
                  className="flex items-center text-gray-300 space-x-2"
                >
                  <input
                    type="checkbox"
                    checked={selectedAmenities.includes(amenity)}
                    onChange={() => toggleAmenity(amenity)}
                    className="w-4 h-4 accent-slate-600"
                  />
                  <span>{amenity}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Buttons */}
          <div className="flex justify-end space-x-2 mt-6">
            <button
              type="button"
              onClick={closeModal}
              className="px-4 py-2 bg-gray-600 text-gray-200 rounded-lg hover:bg-gray-500"
            >
              Cancel
            </button>

            <button
              type="submit"
              className="px-4 py-2 bg-slate-600 text-white rounded-lg hover:bg-slate-700"
            >
              Submit for Approval
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
